import useStats from '../utils/utilsStats';
import Stats from './Stats';


export default function CountrySelector() {
    const { stats: countries, loading, error } = 
    useStats('https://covid19.mathdro.id/api/countries');

    const [selectedountry, setSelectedCountry] = useState('Nigeria');
    
    if (loading) return <p>Loading...</p>;
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error...</p>;

    return (
        <div>
            <h2>Currently Showing {selectedountry}</h2>
            <select>
                {Object.entries(countries.countries).map((
                    [country, code]) => (
                        <option key={code} value={code}>
                            {country}
                        </option>
                    )
                )}
            </select>
            <Stats url='https://covid19.mathdro.id/api/countries/Nigeria'></Stats>

        </div>
    );
}
